import {PayloadAction, createSlice} from '@reduxjs/toolkit'

interface Transaction{
    id: number;
    value: number;
    category: string;
    date: string;
    type: 'income' | 'expense';
    spendingType?: 'strategic' | 'current' | 'midterm';
}

const initialState: Transaction[] = [];

export const transactionsSlice = createSlice({
    name: 'transactions',
    initialState,
    reducers: {
        addTransaction: (state, action: PayloadAction<number>) => {
            state.value += action.payload;
        },
        suspectFromBalance: (state, action: PayloadAction<number>) => {
            state.value -= action.payload
        },

    }
})


export const { addToBalance, suspectFromBalance } = transactionsSlice.actions

export default transactionsSlice.reducer
